mapper.update:
UPDATE
	ctClassMember
SET 
	ctcmName = #{ctcmName}
	,ctcmAlarmReplyNy = #{ctcmAlarmReplyNy}
	,ctcmAlarmReactionNy = #{ctcmAlarmReactionNy}

WHERE 1=1
	AND ctcmSeq = #{ctcmSeq}

mapper.select:
select a.ctcsSeq ,a.ctcsName ,a.ctcsBelongto ,a.ctcsDesc ,a.regDateTime ,a.ctcsCode
from ctClass a
where 1=1 AND a.ctcsSeq = #{ctcsSeq}

mapper.update:
update ctPost 
set ctptView = ctptView + 1 
where ctptSeq = #{ctptSeq}

mapper.insert:
insert into ctAttendance (
ctcmSeq,
ctadRegDateTime
) values (

	#{ctcmSeq},
	#{ctadRegDateTime}
)

mapper.select:
select ctcmSeq
from ctClassMember
where mmSeq = ${mmSeq} and ctcsSeq = ${ctcsSeq}

select ctadRegDateTime
from ctAttendance
where DATE_FORMAT(ctadRegDateTime, '%Y-%m-%d') =  ${attendanceToday}
