mapper.update:
UPDATE
	ctClassMember
SET 
	ctcmName = #{ctcmName}
	,ctcmAlarmReplyNy = #{ctcmAlarmReplyNy}
	,ctcmAlarmReactionNy = #{ctcmAlarmReactionNy}

WHERE 1=1
	AND ctcmSeq = #{ctcmSeq}

mapper.select:
select a.ctcsSeq ,a.ctcsName ,a.ctcsBelongto ,a.ctcsDesc ,a.regDateTime ,a.ctcsCode
from ctClass a
where 1=1 AND a.ctcsSeq = #{ctcsSeq}

mapper.update:
UPDATE 
	ctHomeworkSubmit 
SET 
	cthsScore = #{cthsScore} 
WHERE 1=1
	AND cthpSeq = #{cthpSeq}
	AND cthsSeq = #{cthsSeq}

UPDATE 
	ctHomeworkSubmit 
SET 
	cthsTitle = #{cthsTitle} 
	,cthsDesc = #{cthsDesc} 
WHERE 1=1
	AND cthsSeq = #{cthsSeq}

update ctPost 
set ctptView = ctptView + 1 
where ctptSeq = #{ctptSeq}

mapper.insert:
insert into ctAttendance (
ctcmSeq,
ctadRegDateTime
) values (

	#{ctcmSeq},
	#{ctadRegDateTime}
)

mapper.select:
select ctcmSeq
from ctClassMember
where mmSeq = ${mmSeq} and ctcsSeq = ${ctcsSeq}

select count(*)
from ctAttendance
where ctcmSeq=${ctcmSeq}
and DATE_FORMAT(ctadRegDateTime, '%Y-%m-%d')=#{attendanceToday}

select ctcmName,ctadRegDateTime 
from ctClassMember a 
left join ctAttendance b on a.ctcmSeq = b.ctcmSeq 
where ctcsSeq = #{classSeq}
and ctadRegDateTime > #{startDate} 
and ctadRegDateTime  < #{endDate}
and ctcmName = #{memberName}
